  console.log('Handedness: ' + String(xrControllerProp('HANDEDNESS')));
  console.log('Target Ray Mode: ' + String(xrControllerProp('TARGET_RAY_MODE')));
  console.log('Profiles: ' + String(xrControllerProp('PROFILES')));
